case "$1" in
    start) echo "Starting..." ;;
    stop)  echo "Stopping..." ;;
    *)     echo "Usage: $0 {start|stop}" ;;
esac

file="image.png"
echo ${file%.*}   # image (Remove extension)
echo ${file##*.}  # png (Get extension)
echo ${name:-Default} # Default value if unset

cleanup() {
    rm -rf /tmp/temp_files
    echo "Cleaned up."
}

# Run cleanup on EXIT, SIGINT (Ctrl+C), or Error
trap cleanup EXIT INT TERM
