const FileError = error{ AccessDenied, NotFound };

fn open() FileError!void {
    return FileError.NotFound;
}

// Catching errors
open() catch |err| {
    std.debug.print("Failed: {}", .{err});
};

fn doWork() !void {
    try open(); // Returns error if open() fails
    try write();
}

