// build.zig
pub fn build(b: *std.Build) void {
    const exe = b.addExecutable(.{
        .name = "app",
        .root_source_file = .{ .path = "src/main.zig" },
        .target = b.standardTargetOptions({}),
        .optimize = b.standardOptimizeOption({}),
    });
    b.installArtifact(exe);
}
